mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select t1.*, t2.name categoryName from dish t1, category t2 where t1.category_id = t2.id

    and t1.name like concat('%',#{name},'%')


    and t1.status = #{status}


    and t1.category_id = #{categoryId}

order by update_time desc
